package pages;import com.aventstack.extentreports.ExtentTest;import io.cucumber.java.Scenario;//import listeners.ExtentReportListener;import org.openqa.selenium.By;import org.openqa.selenium.WebDriver;import utilities.ui.BasePage;import utilities.ui.WebDriverInitialization;public class FooterPage {    // Locators    private static final By lilogin = By.linkText("Login");    private static final By liContactUs = By.linkText("Contact us");    private static final By liTermsOfUse = By.linkText("Terms of use");    private static final By liPrivacyPolicy = By.linkText("Privacy policy");    private static final By lblLoginText = By.xpath("//h1[text()='Log in']");    private static final By liContactUsText = By.xpath("//h1[text()='Contact WEX']");    private static final By liTermsOfUseText = By.xpath("//h1[text()='WEX Inc. Terms of Use']");    private static final By liPrivacyPolicyText = By.xpath("//h1[text()='WEX Privacy Notice Summary']");    private static final By liSignup = By.linkText("Sign up");    private static final By LiHome = By.linkText("Home");    private WebDriver driver;    private BasePage basePage;    public Scenario logger;    /* Constructor to get the driver object */    public FooterPage(WebDriver driver) {        this.driver = driver;        basePage = new BasePage(driver);    }    /*     * Method to click on Login Footer link     */    public void clickLoginFooterLink(Scenario logger) {        basePage.userClick(logger, lilogin);    }    /*     * Method to verify presence of Login page title     */    public void verifyLoginPageTitlePresence(Scenario logger) {        basePage.whetherElementPresent(logger, lblLoginText, 2);    }    /*     * Method to click on Contact Us Footer link     */    public void clickContactUs(Scenario logger) {        basePage.userClick(logger, liContactUs);    }    /*     * Method to click on Home Footer link     */    public void clickHome(Scenario logger) {        basePage.userClick(logger, LiHome);    }    /*     * Method to verify presence of ContactUs page title     */    public void verifyContacUsPageTitlePresence(Scenario logger) {        basePage.whetherElementPresent(logger, liContactUsText);    }    /*     * Method to click on Login Footer link     */    public void clickPrivacyPolicy(Scenario logger) {        basePage.userClick(logger, liPrivacyPolicy);    }    /*     * Method to verify presence of Privacy Policy page title     */    public void verifyPrivacyPolicyTitlePresence(Scenario logger) {        basePage.whetherElementPresent(logger, liPrivacyPolicyText);    }    /*     * Method to click on Terms Of Use Footer link     */    public void clickTermsOfUse(Scenario logger) {        basePage.userClick(logger, liTermsOfUse);    }    /*     * Method to verify presence of Terms of use page title     */    public void verifyUserTermsOfUSePageTitlePresence(Scenario logger) {        basePage.whetherElementPresent(logger, liTermsOfUseText);    }    /*    Method to click SignUp in login     */    public void clickSignup(Scenario logger) {        basePage.userClick(logger, liSignup);    }}